contracts/StakeableUpgradeableV2.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @notice Stakeable needs to be inherited by other contracts for Staking capabilities
 */
contract StakeableUpgradeableV2 is OwnableUpgradeable {
    // ============ Mutable Storage ============

    /**
     * @notice
     * a mapping(user => amount)
     */
    mapping(address => uint256) internal stakes;

    // ============ Structs ============

    struct User {
        address addr;
        uint256 amount;
    }

    // ============ Events ============

    /**
     * @notice address is indexed to make it filterable
     */
    event Staked(address indexed user, uint256 amount);
    event UnStaked(address indexed user, uint256 amount);
    event Slashed(address indexed user, uint256 amount);

    // ============ Staking ============

    /**
     * @notice
     *  stake amount for msg.sender
     */
    function _stake(uint256 amount) internal {
        require(amount > 0, "Cannot stake nothing");
        stakes[_msgSender()] += amount;
        emit Staked(_msgSender(), amount);
    }

    /**
     * @notice
     * unstake amount from msg.sender
     */
    function _unstake(uint256 amount) public {
        require(stakes[_msgSender()] >= amount, "_unstake: exceeds balance");
        stakes[_msgSender()] -= amount;
        emit UnStaked(_msgSender(), amount);
    }

    /**
     * @notice
     * slash amount from msg.sender
     */
    function _slash(address account, uint256 amount) public onlyOwner {
        require(stakes[account] >= amount, "_slash: exceeds balance");
        stakes[account] -= amount;
        emit Slashed(account, amount);
    }

    /**
     * @notice
     * gets account's staked amount
     */
    function _stakeOf(address account) public view returns (uint256) {
        // TODO: Implement "Checkpoint" mechanism for `stakes` in the
        // same way ERC20Votes does for `_balances`.
        return stakes[account];
    }

    /**
     * @notice
     * bulk insert user's stake amounts.
     * totalAmount - By providing the totalAmount beforehand we can avoid crucial mistakes.
     */
    function _bulkStake(User[] calldata users, uint256 totalAmount) internal onlyOwner {
        require(users.length > 0, "bulkStake 0 users");
        require(totalAmount > 0, "bulkStake 0 totalAmount");
        uint calculatedAmount = 0;
        for (uint256 i = 0; i < users.length; i++) {
            uint amount = users[i].amount;
            stakes[users[i].addr] += amount;
            calculatedAmount += amount;
            emit Staked(users[i].addr, amount);
        }
        require(calculatedAmount == totalAmount, "incorrect totalAmount");
    }
    /**
     * @notice
     * bulk slash user's stake amounts.
     * totalAmount - By providing the totalAmount beforehand we can avoid crucial mistakes.
     */
    function _bulkSlash(User[] calldata users, uint256 totalAmount) internal onlyOwner {
        require(users.length > 0, "_bulkSlash 0 users");
        require(totalAmount > 0, "_bulkSlash 0 totalAmount");
        uint calculatedAmount = 0;
        for (uint256 i = 0; i < users.length; i++) {
            uint amount = users[i].amount;
            uint userStake = stakes[users[i].addr];
            uint toSlash = amount > userStake ? userStake : amount;
            stakes[users[i].addr] -= toSlash;
            calculatedAmount += amount;
            emit Slashed(users[i].addr, toSlash);
        }
        require(calculatedAmount == totalAmount, "incorrect totalAmount");
    }
}

contracts/GoldenTokenV2.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20VotesUpgradeable.sol";
import "./StakeableUpgradeableV2.sol";

/// @custom:security-contact security@golden.com
contract GoldenTokenV2 is
    ERC20PermitUpgradeable,
    ERC20VotesUpgradeable,
    StakeableUpgradeableV2
{
    function initialize(uint256 initialSupply) public initializer {
        __Ownable_init();
        __ERC20_init("GoldenToken", "GLD");
        __ERC20Permit_init("GoldenToken");
        _mint(_msgSender(), initialSupply);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        super._beforeTokenTransfer(from, to, amount);

        require(
            (from == address(0) ||
                from == owner() ||
                from == address(this) ||
                to == address(this)),
            "ERC20: Not allowed to transfer"
        );
    }

    // ============ Staking ============

    function stake(uint256 _amount) external {
        _stake(_amount);
        transfer(address(this), _amount);
    }

    function unstake(uint256 amount) external {
        _unstake(amount);
        _transfer(address(this), _msgSender(), amount);
    }

    function slash(address account, uint256 amount) public onlyOwner {
        _slash(account, amount);
        transfer(owner(), amount);
    }

    function stakeOf(address account) public view returns(uint256) {
        return _stakeOf(account);
    }

    /**
     * @notice
     * bulk insert user's stake amounts.
     */
    function bulkStake(User[] calldata users, uint256 totalAmount) external onlyOwner {
        _bulkStake(users, totalAmount);
        transfer(address(this), totalAmount);
    }

    /**
      * Voting overrides
     */
    function getVotes(address account) public view override returns (uint256) {
        // We don't want users to lose their vote weight when they stake.
        // So we override `getVotes` to return the sum of token balance and
        // stake.
        return super.getVotes(account) + _stakeOf(account);
    }

    /**
     * The functions below are overrides required by Solidity.
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20Upgradeable, ERC20VotesUpgradeable) {
        super._afterTokenTransfer(from, to, amount);
    }

    // ============ Mint/Burn ============

    function _mint(address to, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._burn(account, amount);
    }
}
