{"defaultCompiler":{"version":"0.8.16"},"sources":{"contracts/token/GoldenToken.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.16;

import '../roles/OwnerRole.sol';
import '../roles/MinterRole.sol';
import '../roles/BurnerRole.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/extensions/draft-ERC20PermitUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20VotesUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import '../roles/TransferRole.sol';

/// @custom:security-contact security@golden.co
contract GoldenToken is
    Initializable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    ERC20PermitUpgradeable,
    ERC20VotesUpgradeable,
    OwnerRole,
    MinterRole,
    BurnerRole,
    TransferRole
{
    function initialize(uint256 initialSupply) public initializer {
        __ERC20_init('GoldenToken', 'GLD');
        __ERC20Burnable_init();
        __ERC20Permit_init('GoldenToken');
        __ERC20Votes_init();
        _addOwner(msg.sender);
        _addGrantsToTransfer(msg.sender);
        _mint(msg.sender, initialSupply);
    }

    function mint(address to, uint256 amount) public onlyMinter {
        _mint(to, amount);
    }

    function burn(uint256 amount) public override onlyBurner {
        _burn(msg.sender, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        override
        onlyGrantedTransfer
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _afterTokenTransfer(address from, address to, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(address to, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._burn(account, amount);
    }
}
","versionPragma":"^0.8.16"},"contracts/roles/OwnerRole.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.16;

contract OwnerRole {
    event OwnerAdded(address indexed addedOwner, address indexed addedBy);
    event OwnerRemoved(address indexed removedOwner, address indexed removedBy);

    struct Role {
        mapping(address => bool) members;
    }

    Role private _owners;

    modifier onlyOwner() {
        require(
            isOwner(msg.sender),
            'OwnerRole: caller does not have the Owner role'
        );
        _;
    }

    function isOwner(address account) public view returns (bool) {
        return _has(_owners, account);
    }

    function addOwner(address account) external onlyOwner {
        _addOwner(account);
    }

    function removeOwner(address account) external onlyOwner {
        _removeOwner(account);
    }

    /// @notice Only administrators should be allowed to update
    /// @dev Grants address owner role
    /// @param account The address that is guaranteed owner authorization
    function _addOwner(address account) internal {
        _add(_owners, account);
        emit OwnerAdded(account, msg.sender);
    }

    /// @notice Only administrators should be allowed to update
    /// @dev Removes an account from being an owner
    /// @param account The address removed as an owner
    function _removeOwner(address account) internal {
        _remove(_owners, account);
        emit OwnerRemoved(account, msg.sender);
    }

    /// @notice Only administrators should be allowed to update
    /// @dev Give an account access to this role
    /// @param role All authorizations for the contract
    /// @param account The address that is guaranteed owner authorization
    function _add(Role storage role, address account) internal {
        require(account != address(0x0), 'Invalid 0x0 address');
        require(!_has(role, account), 'Roles: account already has role');
        role.members[account] = true;
    }

    /// @notice Only administrators should be allowed to update
    /// @dev Remove an account's access to this role
    /// @param role All authorizations for the contract
    /// @param account The address that is guaranteed owner authorization
    function _remove(Role storage role, address account) internal {
        require(_has(role, account), 'Roles: account does not have role');
        role.members[account] = false;
    }

    /// @dev Check if an account is in the set of roles
    /// @param role All authorizations for the contract
    /// @param account The address that is guaranteed owner authorization
    /// @return boolean
    function _has(Role storage role, address account)
        internal
        view
        returns (bool)
    {
        require(account != address(0), 'Roles: account is the zero address');
        return role.members[account];
    }

    uint256[49] private __gap;
}
","versionPragma":"^0.8.16"},"contracts/roles/MinterRole.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.16;

import './OwnerRole.sol';

contract MinterRole is OwnerRole {
    event MinterAdded(address indexed addedMinter, address indexed addedBy);
    event MinterRemoved(
        address indexed removedMinter,
        address indexed removedBy
    );

    Role private _minters;

    modifier onlyMinter() {
        require(
            isMinter(msg.sender),
            'MinterRole: caller does not have the Minter role'
        );
        _;
    }

    function isMinter(address account) public view returns (bool) {
        return _has(_minters, account);
    }

    function _addMinter(address account) internal {
        _add(_minters, account);
        emit MinterAdded(account, msg.sender);
    }

    function _removeMinter(address account) internal {
        _remove(_minters, account);
        emit MinterRemoved(account, msg.sender);
    }

    function addMinter(address account) external onlyOwner {
        _addMinter(account);
    }

    function removeMinter(address account) external onlyOwner {
        _removeMinter(account);
    }

    uint256[49] private __gap;
}
","versionPragma":"^0.8.16"},"contracts/roles/BurnerRole.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.16;

import './OwnerRole.sol';

/// @title BurnerRole Contract
/// @notice Only Owner can update the burner roles
/// @dev Keeps track of burners and can check if an account is authorized
contract BurnerRole is OwnerRole {
    event BurnerAdded(address indexed addedBurner, address indexed addedBy);
    event BurnerRemoved(
        address indexed removedBurner,
        address indexed removedBy
    );

    Role private _burners;

    modifier onlyBurner() {
        require(
            isBurner(msg.sender),
            'BurnerRole: caller does not have the Burner role'
        );
        _;
    }

    function isBurner(address account) public view returns (bool) {
        return _has(_burners, account);
    }

    function _addBurner(address account) internal {
        _add(_burners, account);
        emit BurnerAdded(account, msg.sender);
    }

    function _removeBurner(address account) internal {
        _remove(_burners, account);
        emit BurnerRemoved(account, msg.sender);
    }

    function addBurner(address account) external onlyOwner {
        _addBurner(account);
    }

    function removeBurner(address account) external onlyOwner {
        _removeBurner(account);
    }

    uint256[49] private __gap;
}
","versionPragma":"^0.8.16"},"contracts/roles/TransferRole.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.16;

import './OwnerRole.sol';

contract TransferRole is OwnerRole {
    event TransferGranted(
        address indexed grantedAddress,
        address indexed addedBy
    );
    event TransferRevoked(
        address indexed revokedAddress,
        address indexed revokedBy
    );

    Role private _transfer;

    modifier onlyGrantedTransfer() {
        require(
            hasGrantsToTransfer(msg.sender),
            'Transfer: caller does not have the grants to transfer'
        );
        _;
    }

    function hasGrantsToTransfer(address account) public view returns (bool) {
        return _has(_transfer, account);
    }

    function _addGrantsToTransfer(address account) internal {
        _add(_transfer, account);
        emit TransferGranted(account, msg.sender);
    }

    function _revokeTransfer(address account) internal {
        _remove(_transfer, account);
        emit TransferRevoked(account, msg.sender);
    }

    function grantTransfer(address account) external onlyOwner {
        _addGrantsToTransfer(account);
    }

    function revokeTransfer(address account) external onlyOwner {
        _revokeTransfer(account);
    }

    uint256[49] private __gap;
}
